import React from 'react'
class AddEmp extends React.Component{
    constructor(){
        super();
        this.state={
            full_name:'',
            email:'',
            contact:'',
            address:''
        }
        this.changeHandler.bind
    }
    // Input change handler
    changeHandler(){
        this.setState({
            [event.target.name]:event.target.value
        })
        console.log(this.state)
    }
    // submit form
    submitForm(){
        console.log('Data submitted')
    }
  render(){
  return (
    <table className="table table-bordered">
        <tbody>
    <tr>
    <th scope="col">Full Name</th>
    <td>
        <input name='full_name' onChange={this.changeHandler} type='text' className='form-control'/>
    </td>
    </tr>
    <tr>
    <th scope="col">Contact</th>
    <td>
        <input name='contact' onChange={this.changeHandler} type='text' className='form-control'/>
    </td>
    </tr>
    <tr>
    <th scope="col">Email</th>
    <td>
        <input name='email' onChange={this.changeHandler} type='text' className='form-control'/>
    </td>
    </tr>
    <tr>
    <th scope="col">Address</th>
    <td>
        <input name='address' onChange={this.changeHandler} type='text' className='form-control'/>
    </td>
    </tr>
    <tr>
        <td colSpan={2}>
        <input type='submit' onChange={this.submitForm} className='btn btn-info'/>
        </td>
    </tr>
    </tbody>
</table>
  )
  }
}
export default AddEmp
